using System;
using System.Collections.Generic;
using System.Collections;
using System.Text;
using Peach.Core.Debuggers.DebugEngine;
using System.IO;
using Peach.Core;
using Peach.Core.Dom;
using Peach.Core.Analyzers;
using Peach.Core.Cracker;
using Peach.Core.Debuggers.WindowsSystem;

namespace TestThings
{
	class Program
	{
		bool caughtException = false;

		static void Main(string[] args)
		{
			new Program();
		}

		public Program()
		{
			System.Environment.SetEnvironmentVariable("PEACH", "");
			var dbg = SystemDebugger.CreateProcess(@"C:\Peach3.0\Release\CrashingProgram.exe");
			dbg.HandleAccessViolation = new HandleAccessViolation(HandleAccessViolation);
			dbg.ContinueDebugging = new ContinueDebugging(ContinueDebugging);

			dbg.MainLoop();

			System.Diagnostics.Debugger.Break();
		}

		public bool ContinueDebugging()
		{
			// Terminate debugging on exception
			return !caughtException;
		}

		public void HandleAccessViolation(UnsafeMethods.DEBUG_EVENT e)
		{
			do
			{
				if (e.u.Exception.dwFirstChance != 0)
				{
					// Guard page or illegal op
					if (e.u.Exception.ExceptionRecord.ExceptionCode == 0x80000001 ||
						e.u.Exception.ExceptionRecord.ExceptionCode == 0xC000001D)
					{
						// Internesting!
						Console.Error.WriteLine("HandleAccessViolation: First chance guard page or illegal op");
						break;
					}

					if (e.u.Exception.ExceptionRecord.ExceptionCode == 0xC0000005)
					{
						if (e.u.Exception.ExceptionRecord.ExceptionInformation[0] == 1 &&
							e.u.Exception.ExceptionRecord.ExceptionInformation[1] != 0)
						{
							// is write a/v?
							Console.Error.WriteLine("HandleAccessViolation: First chance write a/v");
							break;
						}

						if (e.u.Exception.ExceptionRecord.ExceptionInformation[0] == 0)
						{
							// is DEP?
							Console.Error.WriteLine("HandleAccessViolation: First chance DEP");
							break;
						}
					}

					// Otherwise not interesting
					return;
				}

				// Second chance we capture all
				Console.Error.WriteLine("HandleAccessViolation: Second chance exception, w00t");
			} while (false);

			caughtException = true;
		}
	}
}

// end
